def minion_game(string):
    #print(string)
    stuart = 0
    kevin = 0
    for i in range(len(string)):
        letters = len(string) - i
        if isVowel(string[i]):
            kevin += letters
        else:
            stuart += letters
    
    if(kevin == stuart):
        print('Draw')
    else:
        if(kevin > stuart):
            print('Kevin ' + str(kevin))
        else:
            print('Stuart ' + str(stuart))
        
def isVowel(char):
    vowels = 'AEIOU'
    return char in vowels

